+import React from "react";
+import { motion } from "framer-motion";
+import { cn } from "@/lib/utils";
+
+interface EnhancedSkeletonProps {
+  className?: string;
+  animated?: boolean;
+  pulse?: boolean;
+  shimmer?: boolean;
+}
+
+export function EnhancedSkeleton({
+  className,
+  animated = true,
+  pulse = true,
+  shimmer = false,
+  ...props
+}: EnhancedSkeletonProps) {
+  const skeletonClasses = cn(
+    "rounded-md bg-gray-200",
+    pulse && "animate-pulse",
+    className
+  );
+
+  if (!animated) {
+    return <div className={skeletonClasses} {...props} />;
+  }
+
+  const variants = {
+    loading: {
+      opacity: [0.6, 1, 0.6],
+      transition: {
+        duration: 1.5,
+        repeat: Infinity,
+        ease: "easeInOut"
+      }
+    }
+  };
+
+  return (
+    <motion.div
+      className={cn(
+        skeletonClasses,
+        shimmer && "relative overflow-hidden bg-gradient-to-r from-gray-200 via-gray-300 to-gray-200 bg-[length:200%_100%]"
+      )}
+      variants={variants}
+      animate="loading"
+      {...props}
+    >
+      {shimmer && (
+        <motion.div
+          className="absolute inset-0 bg-gradient-to-r from-transparent via-white/60 to-transparent"
+          animate={{
+            x: ["-100%", "100%"]
+          }}
+          transition={{
+            duration: 2,
+            repeat: Infinity,
+            ease: "linear"
+          }}
+        />
+      )}
+    </motion.div>
+  );
+}
+
+// Preset skeleton components
+export function CardSkeleton() {
+  return (
+    <div className="bg-white rounded-xl shadow-md p-6 space-y-4">
+      <EnhancedSkeleton className="h-16 w-16 rounded-full" shimmer />
+      <div className="space-y-2">
+        <EnhancedSkeleton className="h-4 w-3/4" shimmer />
+        <EnhancedSkeleton className="h-4 w-1/2" shimmer />
+      </div>
+      <div className="space-y-2">
+        <EnhancedSkeleton className="h-3 w-full" />
+        <EnhancedSkeleton className="h-3 w-5/6" />
+        <EnhancedSkeleton className="h-3 w-4/5" />
+      </div>
+      <EnhancedSkeleton className="h-8 w-20 rounded-full" />
+    </div>
+  );
+}
+
+export function HeroSkeleton() {
+  return (
+    <div className="space-y-6 p-8">
+      <EnhancedSkeleton className="h-12 w-3/4" shimmer />
+      <EnhancedSkeleton className="h-6 w-full" />
+      <EnhancedSkeleton className="h-6 w-5/6" />
+      <div className="flex space-x-4 pt-4">
+        <EnhancedSkeleton className="h-12 w-32 rounded-lg" shimmer />
+        <EnhancedSkeleton className="h-12 w-32 rounded-lg" />
+      </div>
+    </div>
+  );
+}

