+import React, { useState, useEffect } from 'react';
+import { motion, AnimatePresence } from 'framer-motion';
+import { Link } from 'wouter';
+import { Button } from '@/components/ui/button';
+import { 
+  FaArrowRight, 
+  FaShieldAlt, 
+  FaRocket, 
+  FaSatellite, 
+  FaDesktop,
+  FaPlay,
+  FaPause
+} from 'react-icons/fa';
+import { heroStats } from '@/content/home';
+
+// Live data simulation for mission dashboard
+const useLiveData = () => {
+  const [data, setData] = useState({
+    activeMissions: 18,
+    systemsOnline: 247,
+    threatLevel: 'SECURE',
+    networkStatus: 99.7
+  });
+
+  useEffect(() => {
+    const interval = setInterval(() => {
+      setData(prev => ({
+        activeMissions: prev.activeMissions + Math.floor(Math.random() * 3) - 1,
+        systemsOnline: Math.max(240, prev.systemsOnline + Math.floor(Math.random() * 5) - 2),
+        threatLevel: ['SECURE', 'ELEVATED', 'SECURE', 'SECURE'][Math.floor(Math.random() * 4)],
+        networkStatus: Math.max(95, Math.min(100, prev.networkStatus + (Math.random() - 0.5) * 2))
+      }));
+    }, 3000);
+
+    return () => clearInterval(interval);
+  }, []);
+
+  return data;
+};
+
+const MissionDashboard = () => {
+  const liveData = useLiveData();
+  
+  return (
+    <div className="bg-black/40 backdrop-blur-md rounded-2xl border border-teal/30 p-6 space-y-4">
+      <div className="flex items-center justify-between">
+        <h3 className="text-teal font-condensed font-bold text-lg">MISSION COMMAND</h3>
+        <div className="flex items-center space-x-2">
+          <div className="w-2 h-2 bg-green-400 rounded-full animate-pulse"></div>
+          <span className="text-xs text-green-400 font-mono">LIVE</span>
+        </div>
+      </div>
+      
+      <div className="grid grid-cols-2 gap-4">
+        <div className="space-y-2">
+          <div className="text-xs text-white/60 uppercase tracking-wide">Active Missions</div>
+          <motion.div 
+            key={liveData.activeMissions}
+            initial={{ scale: 1.2, color: '#14b8a6' }}
+            animate={{ scale: 1, color: '#ffffff' }}
+            className="text-2xl font-bold font-mono"
+          >
+            {liveData.activeMissions}
+          </motion.div>
+        </div>
+        
+        <div className="space-y-2">
+          <div className="text-xs text-white/60 uppercase tracking-wide">Systems Online</div>
+          <motion.div 
+            key={liveData.systemsOnline}
+            initial={{ scale: 1.2, color: '#14b8a6' }}
+            animate={{ scale: 1, color: '#ffffff' }}
+            className="text-2xl font-bold font-mono"
+          >
+            {liveData.systemsOnline}
+          </motion.div>
+        </div>
+        
+        <div className="space-y-2">
+          <div className="text-xs text-white/60 uppercase tracking-wide">Threat Level</div>
+          <motion.div 
+            key={liveData.threatLevel}
+            initial={{ scale: 1.1 }}
+            animate={{ scale: 1 }}
+            className={`text-sm font-bold ${
+              liveData.threatLevel === 'SECURE' ? 'text-green-400' : 'text-yellow-400'
+            }`}
+          >
+            {liveData.threatLevel}
+          </motion.div>
+        </div>
+        
+        <div className="space-y-2">
+          <div className="text-xs text-white/60 uppercase tracking-wide">Network Status</div>
+          <motion.div 
+            key={Math.floor(liveData.networkStatus * 10)}
+            initial={{ scale: 1.1 }}
+            animate={{ scale: 1 }}
+            className="text-sm font-bold text-green-400"
+          >
+            {liveData.networkStatus.toFixed(1)}%
+          </motion.div>
+        </div>
+      </div>
+    </div>
+  );
+};
+
+const HolographicDisplay = () => {
+  const [activeSystem, setActiveSystem] = useState(0);
+  const [isPlaying, setIsPlaying] = useState(true);
+  
+  const systems = [
+    { icon: FaSatellite, name: 'ISR Platform', status: 'OPERATIONAL' },
+    { icon: FaDesktop, name: 'Command Suite', status: 'ACTIVE' },
+    { icon: FaShieldAlt, name: 'Defense Grid', status: 'SECURED' },
+    { icon: FaRocket, name: 'Launch System', status: 'READY' }
+  ];
+
+  useEffect(() => {
+    if (!isPlaying) return;
+    
+    const interval = setInterval(() => {
+      setActiveSystem(prev => (prev + 1) % systems.length);
+    }, 2500);
+
+    return () => clearInterval(interval);
+  }, [isPlaying, systems.length]);
+
+  return (
+    <div className="relative">
+      <div className="bg-gradient-to-br from-teal/20 to-forest/20 rounded-3xl p-8 border border-teal/30 backdrop-blur-sm">
+        <div className="flex items-center justify-between mb-6">
+          <h3 className="text-white font-condensed font-bold text-lg">SYSTEM OVERVIEW</h3>
+          <button
+            onClick={() => setIsPlaying(!isPlaying)}
+            className="text-teal hover:text-white transition-colors"
+          >
+            {isPlaying ? <FaPause /> : <FaPlay />}
+          </button>
+        </div>
+        
+        <div className="relative h-64 flex items-center justify-center">
+          <AnimatePresence mode="wait">
+            <motion.div
+              key={activeSystem}
+              initial={{ opacity: 0, scale: 0.8, rotateY: -90 }}
+              animate={{ opacity: 1, scale: 1, rotateY: 0 }}
+              exit={{ opacity: 0, scale: 0.8, rotateY: 90 }}
+              transition={{ duration: 0.6, ease: "easeInOut" }}
+              className="text-center"
+            >
+              <div className="relative mb-4">
+                <div className="w-24 h-24 mx-auto bg-teal/20 rounded-full flex items-center justify-center border-2 border-teal/50">
+                  <systems[activeSystem].icon className="text-4xl text-teal" />
+                </div>
+                <div className="absolute inset-0 rounded-full border-2 border-teal/30 animate-ping"></div>
+              </div>
+              <h4 className="text-white font-bold text-xl mb-2">{systems[activeSystem].name}</h4>
+              <div className="text-green-400 text-sm font-mono">{systems[activeSystem].status}</div>
+            </motion.div>
+          </AnimatePresence>
+        </div>
+        
+        <div className="flex justify-center space-x-2 mt-6">
+          {systems.map((_, index) => (
+            <button
+              key={index}
+              onClick={() => {
+                setActiveSystem(index);
+                setIsPlaying(false);
+              }}
+              className={`w-3 h-3 rounded-full transition-all ${
+                index === activeSystem ? 'bg-teal' : 'bg-white/30'
+              }`}
+            />
+          ))}
+        </div>
+      </div>
+    </div>
+  );
+};
+
+export function MissionCommandHero() {
+  return (
+    <section className="relative overflow-hidden min-h-screen flex items-center" aria-label="Mission Command Hero">
+      {/* Advanced Background */}
+      <div className="absolute inset-0">
+        <div className="absolute inset-0 bg-gradient-to-br from-[#02150A] via-[#071F17] to-[#0D4114]" />
+        <div className="absolute inset-0 opacity-30">
+          <div className="absolute inset-0 bg-[radial-gradient(circle_at_1px_1px,#14b8a6_1px,transparent_1px)] [background-size:50px_50px] animate-pulse" />
+        </div>
+        <div className="absolute top-1/4 left-1/3 w-96 h-96 bg-teal/20 rounded-full blur-[120px] animate-pulse" />
+        <div className="absolute bottom-1/4 right-1/3 w-80 h-80 bg-forest/30 rounded-full blur-[100px] animate-pulse" />
+        
+        {/* Scanning lines effect */}
+        <div className="absolute inset-0 opacity-20">
+          <div className="absolute top-0 left-0 w-full h-px bg-gradient-to-r from-transparent via-teal to-transparent animate-pulse" />
+          <div className="absolute top-1/3 left-0 w-full h-px bg-gradient-to-r from-transparent via-teal/50 to-transparent animate-pulse" style={{ animationDelay: '1s' }} />
+          <div className="absolute top-2/3 left-0 w-full h-px bg-gradient-to-r from-transparent via-teal/30 to-transparent animate-pulse" style={{ animationDelay: '2s' }} />
+        </div>
+      </div>
+
+      <div className="relative z-10 section-container px-4 sm:px-6 md:px-8 py-20">
+        <div className="grid lg:grid-cols-2 gap-12 items-center">
+          {/* Enhanced Content */}
+          <motion.div
+            initial={{ opacity: 0, x: -50 }}
+            animate={{ opacity: 1, x: 0 }}
+            transition={{ duration: 0.8, ease: "easeOut" }}
+            className="space-y-8 text-white"
+          >
+            {/* Mission Status Badge */}
+            <motion.div
+              initial={{ opacity: 0, y: 20 }}
+              animate={{ opacity: 1, y: 0 }}
+              transition={{ delay: 0.2 }}
+              className="inline-flex items-center bg-teal/20 backdrop-blur-sm rounded-full px-6 py-3 border border-teal/30"
+            >
+              <FaShieldAlt className="text-teal mr-3" />
+              <span className="text-sm font-medium tracking-wide uppercase">
+                Mission-Critical Defense Systems
+              </span>
+              <div className="ml-3 w-2 h-2 bg-green-400 rounded-full animate-pulse" />
+            </motion.div>
+
+            {/* Main Headline */}
+            <motion.h1 
+              initial={{ opacity: 0, y: 30 }}
+              animate={{ opacity: 1, y: 0 }}
+              transition={{ delay: 0.4 }}
+              className="heading-clamp-1 font-condensed font-bold leading-tight"
+            >
+              Next-Gen Defense
+              <br />
+              <span className="text-teal">Command & Control</span>
+            </motion.h1>
+
+            {/* Enhanced Description */}
+            <motion.p 
+              initial={{ opacity: 0, y: 20 }}
+              animate={{ opacity: 1, y: 0 }}
+              transition={{ delay: 0.6 }}
+              className="text-xl text-white/80 max-w-2xl leading-relaxed"
+            >
+              Deploying AI-powered, quantum-secured defense platforms that integrate seamlessly across multi-domain operations. From edge computing to satellite communications.
+            </motion.p>
+
+            {/* Live Stats Grid */}
+            <motion.div 
+              initial={{ opacity: 0, y: 20 }}
+              animate={{ opacity: 1, y: 0 }}
+              transition={{ delay: 0.8 }}
+              className="grid grid-cols-2 sm:grid-cols-4 gap-4"
+            >
+              {heroStats.map((stat, index) => (
+                <motion.div
+                  key={stat.label}
+                  initial={{ opacity: 0, scale: 0.8 }}
+                  animate={{ opacity: 1, scale: 1 }}
+                  transition={{ delay: 0.8 + index * 0.1 }}
+                  className="bg-black/30 backdrop-blur-sm rounded-xl p-4 border border-teal/20 hover:border-teal/40 transition-all"
+                >
+                  <div className="text-2xl font-bold text-teal font-mono">{stat.value}</div>
+                  <div className="text-xs uppercase tracking-wide text-white/70">{stat.label}</div>
+                </motion.div>
+              ))}
+            </motion.div>
+
+            {/* CTA Buttons */}
+            <motion.div 
+              initial={{ opacity: 0, y: 20 }}
+              animate={{ opacity: 1, y: 0 }}
+              transition={{ delay: 1.0 }}
+              className="flex flex-col sm:flex-row gap-4"
+            >
+              <Link href="/Solutions">
+                <Button className="bg-teal hover:bg-teal/90 text-white font-semibold py-4 px-8 rounded-lg shadow-2xl hover:shadow-teal/30 transition-all duration-300 inline-flex items-center justify-center text-lg focus-ring group">
+                  Deploy Solutions 
+                  <FaArrowRight className="ml-2 group-hover:translate-x-1 transition-transform" />
+                </Button>
+              </Link>
+              <Link href="/Contact">
+                <Button
+                  variant="outline"
+                  className="bg-transparent hover:bg-teal/10 border-2 border-teal/50 hover:border-teal text-white font-semibold py-4 px-8 rounded-lg transition-all duration-300 inline-flex items-center justify-center text-lg focus-ring"
+                >
+                  Request Mission Brief
+                </Button>
+              </Link>
+            </motion.div>
+          </motion.div>
+
+          {/* Interactive Dashboard */}
+          <motion.div
+            initial={{ opacity: 0, x: 50 }}
