@@ -0,0 +1,311 @@
+import React, { useState, useEffect } from 'react';
+import { motion, AnimatePresence } from 'framer-motion';
+import { 
+  FaProjectDiagram, 
+  FaTools, 
+  FaSatellite, 
+  FaUsersCog,
+  FaPlay,
+  FaPause,
+  FaCheckCircle
+} from 'react-icons/fa';
+
+const missionPhases = [
+  {
+    id: 'discovery',
+    title: 'Mission Discovery',
+    subtitle: 'Intelligence Gathering',
+    description: 'Deep-dive analysis of operational requirements, threat assessment, and environmental constraints using AI-powered requirement extraction.',
+    icon: FaProjectDiagram,
+    duration: '2-4 weeks',
+    deliverables: ['Threat Analysis Report', 'Technical Requirements Matrix', 'Environmental Constraints Study'],
+    color: 'from-blue-500 to-cyan-500',
+    bgColor: 'from-blue-500/20 to-cyan-500/20'
+  },
+  {
+    id: 'engineering',
+    title: 'Engineering & Prototyping',
+    subtitle: 'Rapid Development',
+    description: 'Agile development of ruggedized architectures with continuous validation against MIL-STD baselines and real-world testing scenarios.',
+    icon: FaTools,
+    duration: '6-12 weeks',
+    deliverables: ['Prototype Systems', 'Performance Benchmarks', 'Integration Testing Results'],
+    color: 'from-orange-500 to-red-500',
+    bgColor: 'from-orange-500/20 to-red-500/20'
+  },
+  {
+    id: 'qualification',
+    title: 'Qualification & Compliance',
+    subtitle: 'Certification Process',
+    description: 'Comprehensive environmental, EMI/EMC, and security testing with full documentation for military certification and compliance.',
+    icon: FaSatellite,
+    duration: '8-16 weeks',
+    deliverables: ['Certification Documents', 'Test Reports', 'Compliance Matrix'],
+    color: 'from-purple-500 to-indigo-500',
+    bgColor: 'from-purple-500/20 to-indigo-500/20'
+  },
+  {
+    id: 'support',
+    title: 'Lifecycle Support',
+    subtitle: 'Mission Sustainment',
+    description: 'Continuous sustainment, obsolescence management, field engineering support, and technology refresh planning.',
+    icon: FaUsersCog,
+    duration: 'Ongoing',
+    deliverables: ['24/7 Support', 'Maintenance Plans', 'Technology Roadmap'],
+    color: 'from-green-500 to-teal-500',
+    bgColor: 'from-green-500/20 to-teal-500/20'
+  }
+];
+
+const TimelineProgress = ({ activePhase, isPlaying }) => {
+  const progress = ((activePhase + 1) / missionPhases.length) * 100;
+  
+  return (
+    <div className="relative w-full h-2 bg-white/10 rounded-full overflow-hidden">
+      <motion.div
+        className="h-full bg-gradient-to-r from-teal to-cyan-400 rounded-full"
+        initial={{ width: 0 }}
+        animate={{ width: `${progress}%` }}
+        transition={{ duration: 0.8, ease: "easeInOut" }}
+      />
+      {isPlaying && (
+        <motion.div
+          className="absolute top-0 left-0 h-full w-8 bg-gradient-to-r from-transparent via-white/30 to-transparent"
+          animate={{ x: [-32, 400] }}
+          transition={{ duration: 2, repeat: Infinity, ease: "linear" }}
+        />
+      )}
+    </div>
+  );
+};
+
+const PhaseCard = ({ phase, index, isActive, onClick }) => {
+  const Icon = phase.icon;
+  
+  return (
+    <motion.div
+      initial={{ opacity: 0, x: index % 2 === 0 ? -50 : 50 }}
+      whileInView={{ opacity: 1, x: 0 }}
+      viewport={{ once: true }}
+      transition={{ delay: index * 0.2, duration: 0.6 }}
+      onClick={() => onClick(index)}
+      className={`
+        relative cursor-pointer group
+        ${index % 2 === 0 ? 'lg:pr-8' : 'lg:pl-8'}
+      `}
+    >
+      <div className={`
+        relative p-6 rounded-2xl border transition-all duration-500
+        ${isActive 
+          ? `bg-gradient-to-br ${phase.bgColor} border-teal shadow-2xl shadow-teal/20 scale-105` 
+          : 'bg-black/20 border-white/10 hover:border-teal/30 hover:bg-black/30'
+        }
+      `}>
+        {/* Phase Number */}
+        <div className="absolute -top-4 -left-4 w-8 h-8 bg-teal rounded-full flex items-center justify-center text-white font-bold text-sm border-4 border-navy">
+          {index + 1}
+        </div>
+        
+        {/* Icon */}
+        <div className="flex items-center mb-4">
+          <div className={`
+            w-12 h-12 rounded-lg flex items-center justify-center mr-4
+            ${isActive ? 'bg-teal/20 border border-teal/30' : 'bg-white/10 border border-white/20'}
+          `}>
+            <Icon className={`text-xl ${isActive ? 'text-teal' : 'text-white/70'}`} />
+          </div>
+          <div>
+            <h3 className="font-condensed font-bold text-lg text-white">{phase.title}</h3>
+            <p className="text-teal text-sm font-medium">{phase.subtitle}</p>
+          </div>
+        </div>
+        
+        {/* Duration Badge */}
+        <div className="inline-flex items-center bg-white/10 rounded-full px-3 py-1 mb-4">
+          <span className="text-xs text-white/80 font-mono">{phase.duration}</span>
+        </div>
+        
+        {/* Description */}
+        <p className="text-white/80 text-sm leading-relaxed mb-4">
+          {phase.description}
+        </p>
+        
+        {/* Deliverables */}
+        <AnimatePresence>
+          {isActive && (
+            <motion.div
+              initial={{ opacity: 0, height: 0 }}
+              animate={{ opacity: 1, height: 'auto' }}
+              exit={{ opacity: 0, height: 0 }}
+              transition={{ duration: 0.3 }}
+              className="border-t border-white/20 pt-4"
+            >
+              <h4 className="text-teal text-sm font-semibold mb-2">Key Deliverables:</h4>
+              <ul className="space-y-1">
+                {phase.deliverables.map((deliverable, idx) => (
+                  <motion.li
+                    key={deliverable}
+                    initial={{ opacity: 0, x: -10 }}
+                    animate={{ opacity: 1, x: 0 }}
+                    transition={{ delay: idx * 0.1 }}
+                    className="flex items-center text-white/70 text-xs"
+                  >
+                    <FaCheckCircle className="text-green-400 mr-2 text-xs" />
+                    {deliverable}
+                  </motion.li>
+                ))}
+              </ul>
+            </motion.div>
+          )}
+        </AnimatePresence>
+        
+        {/* Active indicator */}
+        {isActive && (
+          <motion.div
+            initial={{ scale: 0 }}
+            animate={{ scale: 1 }}
+            className="absolute -right-2 top-1/2 transform -translate-y-1/2 w-4 h-4 bg-teal rounded-full border-4 border-navy"
+          />
+        )}
+      </div>
+    </motion.div>
+  );
+};
+
+export function MissionTimeline() {
+  const [activePhase, setActivePhase] = useState(0);
