+    name: 'Intrusion Detection',
+    level: 'ACTIVE',
+    description: 'Real-time threat monitoring and response',
+    coverage: '100%',
+    color: 'text-yellow-400',
+    icon: FaExclamationTriangle
+  },
+  {
+    name: 'Network Isolation',
+    level: 'ENABLED',
+    description: 'Segmented network architecture',
+    coverage: '100%',
+    color: 'text-blue-400',
+    icon: FaNetworkWired
+  }
+];
+
+const networkTraffic = [
+  { source: 'command-center', target: 'field-unit-1', type: 'command', priority: 'high' },
+  { source: 'satellite-1', target: 'command-center', type: 'intelligence', priority: 'medium' },
+  { source: 'intelligence-hub', target: 'command-center', type: 'data', priority: 'high' },
+  { source: 'field-unit-2', target: 'command-center', type: 'status', priority: 'low' }
+];
+
+const NetworkNode = ({ node, isActive, onClick, connections }) => {
+  const IconComponent = node.icon;
+  
+  const getStatusColor = () => {
+    switch (node.status) {
+      case 'active': return 'border-green-400 bg-green-400/20';
+      case 'operational': return 'border-blue-400 bg-blue-400/20';
+      case 'monitoring': return 'border-yellow-400 bg-yellow-400/20';
+      case 'standby': return 'border-white/50 bg-white/10';
+      default: return 'border-white/30 bg-white/10';
+    }
+  };
+
+  const getSecurityIcon = () => {
+    switch (node.security) {
+      case 'quantum-encrypted': return '🔒';
+      case 'aes-256': return '🛡️';
+      case 'multi-layer': return '🔐';
+      case 'hardened': return '⚡';
+      default: return '🔓';
+    }
+  };
+
+  return (
+    <motion.div
+      initial={{ opacity: 0, scale: 0 }}
+      animate={{ 
+        opacity: 1, 
+        scale: isActive ? 1.2 : 1,
+        boxShadow: isActive ? `0 0 20px ${node.color.replace('text-', 'rgba(').replace('-400', ', 0.5)')}` : 'none'
+      }}
+      transition={{ duration: 0.3 }}
+      onClick={onClick}
+      className={`
+        absolute transform -translate-x-1/2 -translate-y-1/2 cursor-pointer
+        w-16 h-16 rounded-full border-2 flex flex-col items-center justify-center
+        backdrop-blur-sm transition-all duration-300 group
+        ${getStatusColor()}
+      `}
+      style={{ left: `${node.position.x}%`, top: `${node.position.y}%` }}
+    >
+      <IconComponent className={`text-xl ${node.color}`} />
+      <div className="text-xs mt-1">{getSecurityIcon()}</div>
+      
+      {/* Node info tooltip */}
+      <div className="absolute -bottom-16 left-1/2 transform -translate-x-1/2 bg-black/80 backdrop-blur-sm rounded-lg p-2 border border-white/20 opacity-0 group-hover:opacity-100 transition-opacity whitespace-nowrap z-10">
+        <div className="text-white text-xs font-semibold">{node.name}</div>
+        <div className={`text-xs ${node.color}`}>{node.status.toUpperCase()}</div>
+        <div className="text-white/60 text-xs">{node.security}</div>
+      </div>
+      
+      {/* Pulse animation for active nodes */}
+      {node.status === 'active' && (
+        <motion.div
+          animate={{ scale: [1, 1.5, 1], opacity: [0.5, 0, 0.5] }}
+          transition={{ duration: 2, repeat: Infinity }}
+          className="absolute inset-0 rounded-full border-2 border-green-400"
+        />
+      )}
+    </motion.div>
+  );
+};
+
+const NetworkConnection = ({ from, to, isActive, type }) => {
+  const fromNode = networkNodes.find(n => n.id === from);
+  const toNode = networkNodes.find(n => n.id === to);
+  
+  if (!fromNode || !toNode) return null;
+  
+  const getConnectionColor = () => {
+    switch (type) {
+      case 'command': return '#ef4444'; // red
+      case 'intelligence': return '#3b82f6'; // blue
+      case 'data': return '#10b981'; // green
+      case 'status': return '#f59e0b'; // yellow
+      default: return '#6b7280'; // gray
+    }
+  };
+
+  return (
+    <svg className="absolute inset-0 w-full h-full pointer-events-none">
+      <motion.line
+        x1={`${fromNode.position.x}%`}
+        y1={`${fromNode.position.y}%`}
+        x2={`${toNode.position.x}%`}
+        y2={`${toNode.position.y}%`}
+        stroke={getConnectionColor()}
+        strokeWidth={isActive ? "3" : "1"}
+        strokeOpacity={isActive ? 0.8 : 0.3}
+        strokeDasharray={isActive ? "5,5" : "none"}
+        initial={{ pathLength: 0 }}
+        animate={{ pathLength: 1 }}
+        transition={{ duration: 1, delay: Math.random() * 0.5 }}
+      />
+      
+      {/* Data flow animation */}
+      {isActive && (
+        <motion.circle
+          r="3"
+          fill={getConnectionColor()}
+          initial={{ 
+            cx: `${fromNode.position.x}%`, 
+            cy: `${fromNode.position.y}%` 
+          }}
+          animate={{ 
+            cx: `${toNode.position.x}%`, 
+            cy: `${toNode.position.y}%` 
+          }}
+          transition={{ 
+            duration: 2, 
+            repeat: Infinity, 
+            ease: "linear" 
+          }}
+        />
+      )}
+    </svg>
+  );
+};
+
+const SecurityProtocol = ({ protocol, index }) => {
+  const IconComponent = protocol.icon;
+  
+  return (
+    <motion.div
+      initial={{ opacity: 0, x: -20 }}
+      animate={{ opacity: 1, x: 0 }}
+      transition={{ delay: index * 0.1, duration: 0.6 }}
+      className="bg-black/40 backdrop-blur-sm border border-white/10 rounded-lg p-4 hover:border-teal/30 transition-all"
+    >
+      <div className="flex items-center justify-between mb-3">
+        <div className="flex items-center space-x-3">
