+import { useEffect, useState } from "react";
+import { motion, AnimatePresence } from "framer-motion";
+
+// Hero background images with their respective data
+const backgroundImages = [
+    {
+        image: "/assets/carousel1.jpg",
+        alt: "Defense technology engineering visualization",
+    },
+    {
+        image: "/assets/carousel2.png",
+        alt: "Edge compute and secure storage platforms showcase",
+    },
+];
+
+interface HeroBackgroundProps {
+    autoPlay?: boolean;
+    autoPlayInterval?: number;
+    className?: string;
+}
+
+export function HeroBackground({
+    autoPlay = true,
+    autoPlayInterval = 8000,
+    className = "",
+}: HeroBackgroundProps) {
+    const [currentImageIndex, setCurrentImageIndex] = useState(0);
+    const [imageLoaded, setImageLoaded] = useState(false);
+
+    // Auto-play logic
+    useEffect(() => {
+        if (!autoPlay) return;
+        
+        const interval = setInterval(() => {
+            setCurrentImageIndex((prev) => (prev + 1) % backgroundImages.length);
+        }, autoPlayInterval);
+
+        return () => clearInterval(interval);
+    }, [autoPlay, autoPlayInterval]);
+
+    // Preload images
+    useEffect(() => {
+        backgroundImages.forEach((item) => {
+            const img = new Image();
+            img.src = item.image;
+        });
+    }, []);
+
+    return (
+        <div className={`absolute inset-0 ${className}`}>
+            <AnimatePresence mode="wait">
+                <motion.div
+                    key={currentImageIndex}
+                    initial={{ opacity: 0 }}
+                    animate={{ opacity: 1 }}
+                    exit={{ opacity: 0 }}
+                    transition={{ duration: 1.2, ease: "easeInOut" }}
+                    className="absolute inset-0"
+                >
+                    <div
+                        className="absolute inset-0 bg-cover bg-center bg-no-repeat"
+                        style={{
+                            backgroundImage: `url(${backgroundImages[currentImageIndex].image})`,
+                        }}
+                    />
+                    {/* Dark overlay for text readability */}
+                    <div className="absolute inset-0 bg-gradient-to-br from-black/70 via-black/60 to-black/50" />
+                    {/* Additional overlay for defense theme */}
+                    <div className="absolute inset-0 bg-gradient-to-br from-[#02150A]/80 via-[#071F17]/70 to-[#0D4114]/60" />
+                </motion.div>
+            </AnimatePresence>
+            
+            {/* Decorative elements */}
+            <div className="absolute inset-0 opacity-20 bg-[radial-gradient(circle_at_1px_1px,#ffffff0d,transparent_1.6px)] [background-size:26px_26px]" />
+            <div className="absolute top-1/3 left-1/4 w-[38rem] h-[38rem] bg-teal/20 rounded-full blur-[200px]" />
+            <div className="absolute bottom-0 right-0 w-[28rem] h-[28rem] bg-forest/20 rounded-full blur-[180px]" />
+        </div>
+    );
+}

