import React from 'react';
import { Layout } from '@/components/Layout';
import { HomeHero } from './sections/HomeHero';
import { HomeCapabilities } from './sections/HomeCapabilities';
import { HomeProcess } from './sections/HomeProcess';
import { HomeSolutions } from './sections/HomeSolutions';
import { HomeAssurance } from './sections/HomeAssurance';
import { HomeCTA } from './sections/HomeCTA';
import { TrustedPartners } from '@/components/TrustedPartners';
import { Button } from '@/components/ui/button';
import { Link } from 'wouter';
import { FaArrowRight } from 'react-icons/fa';
import {
  homeSeo,
  homeStructuredData,
  partners
} from '@/content/home';

export const metadata = {
  ...homeSeo
};

export function HomePage() {
  return (
    <Layout
      title={homeSeo.title}
      description={homeSeo.description}
      ogType={homeSeo.ogType}
      keywords={homeSeo.keywords}
      structuredData={JSON.stringify(homeStructuredData)}
    >
      <HomeHero />
      <HomeCapabilities />
      <HomeProcess />
      <HomeSolutions />
      <HomeAssurance />
      <TrustedPartners
        partners={partners}
        cta={(
          <Link href="/About">
            <Button
              variant="outline"
              className="bg-white hover:bg-teal hover:text-white border-2 border-teal text-navy font-bold py-3 px-6 rounded-lg transition-all duration-300 focus-ring"
            >
              Learn More About Our Partnerships <FaArrowRight className="ml-2" />
            </Button>
          </Link>
        )}
      />
      <HomeCTA />
    </Layout>
  );
}

export default HomePage;

