    Carousel,
    CarouselContent,
    CarouselItem,
    CarouselNext,
    CarouselPrevious,
    type CarouselApi,
} from "@/components/ui/carousel";
import { cn } from "@/lib/utils";
import {useEffect, useRef, useState} from "react";

// Sample carousel items with captions (could be externalized later)
const carouselItems = [
    {
        image: "/assets/carousel1.jpg",
        alt: "Defense technology engineering visualization",
        title: "Mission-Ready Technology",
        description: "Rugged, secure and high-performance systems engineered for extreme environments.",
    },
    {
        image: "/assets/carousel2.png",
        alt: "Edge compute and secure storage platforms showcase",
        title: "Edge Compute & Secure Data",
        description: "Enabling tactical superiority with modular AI inference and encrypted storage.",
    },
];

interface HeroCarouselProps {
    autoPlay?: boolean;
    autoPlayInterval?: number;
    showControls?: boolean;
    showIndicators?: boolean;
    showCaptions?: boolean;
    className?: string;
}

export function HeroCarousel({
                                 autoPlay = true,
                                 autoPlayInterval = 6000,
                                 showControls = true,
                                 showIndicators = true,
                                 showCaptions = true,
                                 className,
                             }: HeroCarouselProps) {
    const [api, setApi] = useState<CarouselApi>();
    const [currentSlide, setCurrentSlide] = useState(0);
    const prefersReducedMotion = usePrefersReducedMotion();
    const intervalRef = useRef<number | null>(null);

    // Auto-play logic respecting reduced motion
    useEffect(() => {
        if (!api || !autoPlay || prefersReducedMotion) return;
        if (intervalRef.current) window.clearInterval(intervalRef.current);
        intervalRef.current = window.setInterval(() => {
            api.scrollNext();
        }, autoPlayInterval);
        return () => {
            if (intervalRef.current) window.clearInterval(intervalRef.current);
        };
    }, [api, autoPlay, autoPlayInterval, prefersReducedMotion]);

    // Track current slide
    useEffect(() => {
        if (!api) return;
        const onSelect = () => setCurrentSlide(api.selectedScrollSnap());
        api.on("select", onSelect);
        onSelect();
        return () => { api.off("select", onSelect); };
    }, [api]);

    const scrollToSlide = (index: number) => api?.scrollTo(index);

    return (
        <div
            className={cn("relative w-full", className)}
            role="region"
            aria-roledescription="carousel"
            aria-label="Featured technology solutions"
        >
            <Carousel
                className="w-full"
                setApi={setApi}
                opts={{ align: "center", loop: true }}
            >
                <CarouselContent className="-ml-2 md:-ml-4">
                    {carouselItems.map((item, index) => (
                        <CarouselItem key={index} className="pl-2 md:pl-4">
                            <div className="w-full">
                                <figure
                                    className="relative w-full aspect-[4/5] xs:aspect-[5/4] sm:aspect-[4/3] md:aspect-[5/3] lg:aspect-[2/1] overflow-hidden rounded-lg sm:rounded-xl md:rounded-2xl group bg-gradient-to-br from-charcoal/90 via-navy/80 to-forest/70 shadow-xl md:shadow-2xl"
                                >
                                    {/* Image with graceful loading and better fit */}
                                    <ImageWithPlaceholder
                                        src={item.image}
                                        alt={item.alt}
                                        priority={index === 0}
                                    />
                                    {showCaptions && (
                                        <figcaption className="pointer-events-none absolute inset-0 flex flex-col justify-end p-4 xs:p-5 sm:p-6 md:p-8 lg:p-10 bg-gradient-to-t from-black/85 via-black/50 to-transparent text-white">
                                            <h2 className="text-lg xs:text-xl sm:text-2xl md:text-3xl lg:text-4xl font-condensed font-bold mb-1 xs:mb-2 sm:mb-3 drop-shadow-lg text-balance">
                                                {item.title}
                                            </h2>
                                            <p className="text-xs xs:text-sm sm:text-base md:text-lg opacity-90 sm:opacity-95 text-balance leading-snug sm:leading-relaxed">
                                                {item.description}
                                            </p>
                                        </figcaption>
                                    )}
                                </figure>
                            </div>
                        </CarouselItem>
                    ))}
                </CarouselContent>

                {showControls && (
                    <>
                        <CarouselPrevious className="hidden md:flex left-2 md:left-4 lg:left-6 bg-white/10 backdrop-blur-md border-white/20 hover:bg-white/20 h-8 w-8 md:h-10 md:w-10" aria-label="Previous slide" />
                        <CarouselNext className="hidden md:flex right-2 md:right-4 lg:right-6 bg-white/10 backdrop-blur-md border-white/20 hover:bg-white/20 h-8 w-8 md:h-10 md:w-10" aria-label="Next slide" />
                    </>
                )}
            </Carousel>

            {showIndicators && (
                <div className="flex justify-center gap-1.5 sm:gap-2 mt-4 sm:mt-6" aria-label="Slide indicators">
                    {carouselItems.map((_, index) => {
                        const active = currentSlide === index;
                        return (
                            <button
                                key={index}
                                onClick={() => scrollToSlide(index)}
                                className={cn(
                                    "h-1.5 sm:h-2 rounded-full transition-all duration-300 focus-ring",
                                    active ? "bg-teal w-6 sm:w-8" : "bg-white/40 hover:bg-white/60 w-1.5 sm:w-2"
                                )}
                                aria-label={`Go to slide ${index + 1}`}
                                aria-current={active}
                            />
                        );
                    })}
                </div>
            )}
        </div>
    );
}

/* Hook to detect prefers-reduced-motion */
function usePrefersReducedMotion() {
    const [prefers, setPrefers] = useState(false);
    useEffect(() => {
        const media = window.matchMedia('(prefers-reduced-motion: reduce)');
        const update = () => setPrefers(media.matches);
        update();
        media.addEventListener('change', update);
        return () => media.removeEventListener('change', update);
    }, []);
    return prefers;
}

/* Progressive image component with better aspect ratio handling */
interface ImgProps { src: string; alt: string; priority?: boolean; }
function ImageWithPlaceholder({ src, alt, priority }: ImgProps) {
    const [loaded, setLoaded] = useState(false);
    return (
        <>
            {/* Skeleton loader with shimmer effect */}
            <div className={cn(
                "absolute inset-0 bg-gradient-to-br from-charcoal/50 via-navy/40 to-forest/30 skeleton",
                loaded && "hidden"
            )}>
                <div className="absolute inset-0 bg-gradient-to-r from-transparent via-white/5 to-transparent animate-shimmer" />
            </div>
            
            {/* Main image - always cover to fill space completely */}
            <img
                src={src}
                alt={alt}
