                onLoad={() => setLoaded(true)}
                className={cn(
                    "w-full h-full object-cover object-center transition-all duration-700 ease-out",
                    "group-hover:scale-[1.02]",
                    loaded ? "opacity-100" : "opacity-0"
                )}
                draggable={false}
            />
            
            {/* Subtle vignette overlay for better text readability */}
