import React from "react";
import { Link } from "wouter";
import { Layout } from "@/components/Layout";
import { motion, useReducedMotion } from "framer-motion";
import { Button } from "@/components/ui/button";
import { FaArrowRight, FaShieldAlt, FaRegLightbulb } from "react-icons/fa";
import { cn } from "@/lib/utils";
import stealthEdge from "../assets/products/stealth-edge.png";
import ruggedPanel from "../assets/products/rugged-panel.png";
import tacticalServer from "../assets/products/tactical-server.png";

// Retain structured SEO data
const homePageStructuredData = {
    '@context': 'https://schema.org',
    '@type': 'WebPage',
    name: 'TRINETHRA DEFENTECH - Advanced Defense Technology Solutions',
    description: 'Leading provider of military-grade defense technology including cable harness systems, rugged storage solutions, edge server platforms, and panel PCs for defense and security operations.',
    keywords: 'TRINETHRA DEFENTECH, defense technology, cable harness, storage solutions, edge server, panel pc, military systems, tactical equipment',
    inLanguage: 'en-US',
    mainEntity: {
        '@type': 'Organization',
        name: 'TRINETHRA DEFENTECH',
        url: 'https://trinethra-defentech.com',
        logo: 'https://trinethra-defentech.com/logo.png',
        description: 'Premier provider of advanced defense technology solutions engineered for mission-critical environments.',
        sameAs: [
            'https://linkedin.com/company/trinethra-defentech',
            'https://twitter.com/trinethra_def'
        ]
    }
};

const keyMetrics = [
    { value: "18", label: "Mission Programs" },
    { value: ">250", label: "Systems Deployed" },
    { value: "9/10", label: "Client Satisfaction" },
    { value: "24/7", label: "Field Support" }
] as const;

const productShowcase = [
    {
        name: "StealthEdge ISR Suite",
        description: "GPU-accelerated mission computer delivering real-time intelligence, surveillance & reconnaissance.",
        image: stealthEdge,
        specs: ["8x MIL-STD-1553", "Quad 10G Fiber", "NVIDIA RTX GPU"],
        link: "/solutions#isr"
    },
    {
        name: "TitanShield Rugged Panel",
        description: "Battlefield-ready 18\" panel PC with secure comms and multi-domain interoperability.",
        image: ruggedPanel,
        specs: ["IP67 Sealed", "MIL-STD-461G", "TEMPEST Ready"],
        link: "/solutions#command"
    },
    {
        name: "Aegis Tactical Server",
        description: "Deployable edge server optimized for data fusion, analytics, and hardened storage.",
        image: tacticalServer,
        specs: ["SWaP-Optimized", "Redundant NVMe", "Zero-Trust Security"],
        link: "/solutions#sustainment"
    }
] as const;

const capabilityHighlights = [
    {
        title: "Mission Proven Engineering",
        description: "MIL-STD qualified systems with environmental, EMI/EMC, and cyber assurance.",
        icon: FaShieldAlt
    },
    {
        title: "Rapid Field Deployment",
        description: "Modular architectures that integrate into existing defense infrastructure in weeks, not months.",
        icon: FaRegLightbulb
    }
] as const;

const lifecyclePhases = [
    {
        title: "Mission Discovery",
        body: "Jointly define operational requirements, environmental constraints, and interoperability needs.",
        indicator: "Phase 01"
    },
    {
        title: "Engineering & Prototyping",
        body: "Iterate rugged architectures, execute MIL-STD testing, and validate integration baselines.",
        indicator: "Phase 02"
    },
    {
        title: "Qualification & Compliance",
        body: "Deliver certification evidence, cyber accreditation, and full technical documentation.",
        indicator: "Phase 03"
    },
    {
        title: "Lifecycle Sustainment",
        body: "Provide obsolescence management, field service engineering, and secure update pipelines.",
        indicator: "Phase 04"
    }
] as const;

const partnerOrganizations = ["ADE-DRDO", "CABS-DRDO", "GTRE-DRDO", "MTRDC-DRDO", "HAL", "ISRO"] as const;

export default function Home() {
    const prefersReducedMotion = useReducedMotion();

    const fadeUp = {
        hidden: { opacity: 0, y: prefersReducedMotion ? 0 : 32 },
        visible: {
            opacity: 1,
            y: 0,
            transition: { duration: 0.65, ease: "easeOut" }
        }
    } as const;

    const fadeCard = {
        hidden: { opacity: 0, scale: prefersReducedMotion ? 1 : 0.96 },
        visible: {
            opacity: 1,
            scale: 1,
            transition: { duration: 0.45, ease: "easeOut" }
        }
    } as const;

    const stagger = {
        hidden: { opacity: 0 },
        visible: {
            opacity: 1,
            transition: { staggerChildren: prefersReducedMotion ? 0 : 0.14, delayChildren: prefersReducedMotion ? 0 : 0.1 }
        }
    } as const;

    return (
        <Layout
            title="TRINETHRA DEFENTECH | Advanced Defense Technology Solutions"
            description="Leading provider of military-grade cable harness systems, rugged storage solutions, edge server compute platforms, and panel PCs for defense and security operations. Engineered for mission-critical environments."
            ogType="website"
            keywords="TRINETHRA DEFENTECH, defense technology, cable harness, storage solutions, edge server, panel pc, defense-grade hardware"
            structuredData={JSON.stringify(homePageStructuredData)}
        >
            {/* HERO */}
            <section className="relative overflow-hidden bg-gradient-to-br from-[#020C0A] via-[#041C17] to-[#083221] text-white" aria-labelledby="hero-heading">
                <div className="absolute inset-0 opacity-20 bg-[radial-gradient(circle_at_1px_1px,rgba(255,255,255,0.08),transparent_1.6px)] [background-size:26px_26px]" aria-hidden />
                <div className="absolute top-1/3 -left-24 sm:left-8 w-[460px] h-[460px] bg-teal/25 rounded-full blur-[220px]" aria-hidden />
                <div className="absolute bottom-0 right-0 w-[360px] h-[360px] bg-forest/25 rounded-full blur-[180px]" aria-hidden />
                <div className="relative z-10 mx-auto max-w-7xl px-4 sm:px-6 lg:px-12 pt-24 pb-20 sm:pt-28 lg:pt-36 lg:pb-32">
                    <motion.div
                        initial="hidden"
                        animate="visible"
                        variants={stagger}
                        className="grid items-center gap-14 lg:grid-cols-[1.1fr_0.9fr]"
                    >
                        <motion.div variants={fadeUp} className="space-y-6">
                            <span className="inline-flex items-center gap-2 rounded-full border border-white/15 bg-white/10 px-4 py-2 text-xs font-semibold uppercase tracking-[0.2em] text-white/80">
                                <FaShieldAlt className="text-teal" /> Mission-Proven Technology
                            </span>
                            <h1 id="hero-heading" className="font-condensed text-[clamp(2.5rem,6vw,3.8rem)] font-bold leading-[1.05] text-balance">
                                Ruggedized Intelligence for Decisive Missions
                            </h1>
                            <p className="text-base sm:text-lg text-white/80 leading-relaxed max-w-xl">
                                TRINETHRA DEFENTECH delivers sovereign defense systems that pair hardened hardware with secure software, enabling commanders to project tactical advantage across land, sea, air, and space.
                            </p>
                            <div className="grid gap-4 sm:grid-cols-2">
                                {capabilityHighlights.map((item) => {
                                    const Icon = item.icon;
                                    return (
                                        <motion.div
                                            key={item.title}
                                            variants={fadeCard}
                                            className="rounded-2xl border border-white/15 bg-white/10 p-5 backdrop-blur-md"
                                        >
                                            <div className="mb-3 flex items-center gap-3 text-sm font-semibold tracking-wide uppercase text-white">
                                                <Icon className="text-teal" /> {item.title}
                                            </div>
                                            <p className="text-sm text-white/75 leading-relaxed">{item.description}</p>
                                        </motion.div>
                                    );
                                })}
                            </div>
                            <div className="flex flex-col gap-4 sm:flex-row">
                                <Link href="/solutions">
                                    <Button className="w-full sm:w-auto bg-teal hover:bg-teal/90 text-white shadow-lg shadow-teal/30 focus-ring">
                                        Explore Mission Platforms <FaArrowRight className="ml-2" />
                                    </Button>
                                </Link>
                                <Link href="/contact">
                                    <Button
                                        variant="outline"
                                        className="w-full sm:w-auto border-white/20 bg-transparent text-white hover:bg-white/10 focus-ring"
                                    >
                                        Request a Threat Assessment
                                    </Button>
                                </Link>
                            </div>
                        </motion.div>
                        <motion.div variants={fadeCard} className="relative overflow-hidden rounded-3xl border border-white/15 bg-gradient-to-br from-white/5 to-white/0 p-[1px]">
                            <div className="rounded-[calc(theme(borderRadius.3xl))] bg-black/60 p-3 sm:p-4 md:p-6">
                                <div className="relative overflow-hidden rounded-2xl border border-white/10 bg-gradient-to-tr from-black via-black/80 to-black/30">
                                    <video
                                        className="h-full w-full object-cover"
                                        autoPlay
                                        muted
                                        loop
                                        playsInline
                                        aria-label="Mission systems deployment montage"
                                    >
                                        <source src="/assets/home/mission-systems.mp4" type="video/mp4" />
                                    </video>
                                    <div className="absolute inset-0 bg-gradient-to-t from-black/80 via-transparent to-black/40" />
                                    <div className="absolute inset-x-0 bottom-0 p-6 sm:p-8">
                                        <p className="text-sm uppercase tracking-[0.35em] text-white/70">Operational Readiness</p>
                                        <h2 className="mt-2 text-2xl sm:text-3xl font-condensed font-semibold text-white">
                                            Secure. Survivable. Sustainment-Ready.
                                        </h2>
                                    </div>
                                </div>
                            </div>
                        </motion.div>
                    </motion.div>
                    <motion.div
                        variants={stagger}
                        initial="hidden"
                        animate="visible"
                        className="mt-16 grid gap-4 rounded-2xl border border-white/10 bg-black/40 p-6 backdrop-blur-sm sm:grid-cols-2 lg:grid-cols-4"
                    >
                        {keyMetrics.map((metric) => (
