import React from 'react';
import { motion } from 'framer-motion';
import { Link } from 'wouter';
import { Button } from '@/components/ui/button';
import { HeroCarousel } from '@/components/HeroCarousel';
import { heroStats } from '@/data/home';
import { FaArrowRight, FaShieldAlt } from 'react-icons/fa';

const fadeInUp = {
  hidden: { opacity: 0, y: 30 },
  visible: { opacity: 1, y: 0, transition: { duration: 0.6, ease: 'easeOut' } }
};

const stagger = {
  hidden: { opacity: 0 },
  visible: { opacity: 1, transition: { staggerChildren: 0.15, delayChildren: 0.1 } }
};

export function HomeHero() {
  return (
    <section className="relative overflow-hidden" aria-label="Hero">
      <div className="absolute inset-0 bg-gradient-to-br from-[#02150A] via-[#071F17] to-[#0D4114]">
        <div className="absolute inset-0 opacity-20 bg-[radial-gradient(circle_at_1px_1px,#ffffff0d,transparent_1.6px)] [background-size:26px_26px]" />
        <div className="absolute top-1/3 left-1/4 w-[38rem] h-[38rem] bg-teal/40 rounded-full blur-[200px]" />
        <div className="absolute bottom-0 right-0 w-[28rem] h-[28rem] bg-forest/40 rounded-full blur-[180px]" />
      </div>
      <div className="relative z-10 section-container px-4 sm:px-6 md:px-8 pt-24 pb-16 sm:pt-28 sm:pb-20 lg:pt-32 lg:pb-28">
        <div className="grid lg:grid-cols-[0.95fr_1.05fr] gap-8 sm:gap-10 md:gap-12 lg:gap-16 items-center">
          {/* Text */}
          <motion.div initial="hidden" animate="visible" variants={stagger} className="space-y-6 sm:space-y-8 lg:space-y-10 text-white">
            <motion.div variants={fadeInUp} className="inline-flex items-center bg-white/10 backdrop-blur-sm rounded-full px-5 py-2 border border-white/10">
              <FaShieldAlt className="text-teal mr-2" />
              <span className="text-xs sm:text-sm font-medium tracking-wide uppercase">Mission Proven Defense Engineering</span>
            </motion.div>
            <motion.h1 variants={fadeInUp} className="text-4xl sm:text-5xl xl:text-6xl font-condensed font-bold leading-tight tracking-tight">
              Engineered Superiority for <span className="text-teal">Mission Success</span>
            </motion.h1>
            <motion.p variants={fadeInUp} className="text-lg sm:text-xl text-white/80 max-w-2xl leading-relaxed">
              Delivering ruggedized, cyber-resilient systems integrating compute, storage & edge intelligence for joint operations across land, sea, air & space.
            </motion.p>
            <motion.div variants={fadeInUp} className="grid grid-cols-2 sm:grid-cols-4 gap-5">
              {heroStats.map((s) => (
                <div key={s.label} className="bg-white/5 backdrop-blur-sm rounded-xl p-4 border border-white/10">
                  <div className="text-2xl font-bold text-teal">{s.value}</div>
                  <div className="text-[10px] sm:text-xs uppercase tracking-wide text-white/70">{s.label}</div>
                </div>
              ))}
            </motion.div>
            <motion.div variants={fadeInUp} className="flex flex-col sm:flex-row gap-4">
              <Link href="/Solutions">
                <Button className="bg-teal hover:bg-teal/90 text-white font-semibold py-4 px-8 rounded-lg shadow-2xl hover:shadow-teal/30 transition-all duration-300 inline-flex items-center justify-center text-base sm:text-lg focus-ring">
                  Explore Mission Platforms <FaArrowRight className="ml-2" />
                </Button>
              </Link>
              <Link href="/Contact">
                <Button variant="outline" className="bg-transparent hover:bg-white/10 border border-white/20 text-white font-semibold py-4 px-8 rounded-lg transition-all duration-300 inline-flex items-center justify-center text-base sm:text-lg focus-ring">
                  Request a Defense Assessment
                </Button>
              </Link>
            </motion.div>
          </motion.div>
          {/* Carousel */}
          <motion.div 
            initial={{ opacity: 0, x: 60 }} 
            animate={{ opacity: 1, x: 0 }} 
            transition={{ duration: 0.9, delay: 0.2 }} 
            className="relative w-full"
          >
            <div className="relative rounded-2xl sm:rounded-3xl overflow-hidden border border-white/20 shadow-[0_20px_80px_rgba(0,0,0,0.4)] sm:shadow-[0_25px_100px_rgba(0,0,0,0.5)] backdrop-blur-sm bg-gradient-to-br from-white/5 to-white/0 p-2 sm:p-3">
              <HeroCarousel showIndicators showControls className="bg-transparent" />
            </div>
          </motion.div>
        </div>
      </div>
    </section>
  );
}
