                                        </div>
                                        <h2 className="font-condensed font-bold text-navy leading-snug [text-wrap:balance] text-[clamp(1.25rem,3.5vw,1.75rem)] uppercase tracking-tight">
                                            Industry Certifications
                                        </h2>
                                    </div>
                                    <div className="text-[clamp(0.625rem,1.5vw,0.75rem)] font-mono text-charcoal/60 tracking-wider whitespace-nowrap">
                                        06 ACTIVE CERTIFICATIONS
                                    </div>
                                </div>

                                {/* Grid */}
                                <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-[clamp(1rem,2.5vw,1.25rem)]">
                                    {industryCertifications.map((cert, index) => (
                                        <div
                                            key={index}
                                            className="relative bg-white p-[clamp(0.75rem,2vw,1rem)] rounded border-l-4 border-teal shadow-[0_2px_10px_rgba(33,128,141,0.08)] hover:shadow-[0_4px_20px_rgba(33,128,141,0.15)] transition-all duration-300 hover:translate-x-1 min-w-0 group overflow-hidden"
                                        >
                                            {/* Scan line effect */}
                                            <div className="absolute inset-0 bg-gradient-to-b from-transparent via-teal/5 to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-500 pointer-events-none" />

                                            <div className="relative flex items-start justify-between gap-[clamp(0.5rem,2vw,0.75rem)] mb-[clamp(0.375rem,1.5vw,0.5rem)]">
                                                <div className="font-black text-navy text-[clamp(0.625rem,1.5vw,0.875rem)] tracking-wider uppercase font-condensed flex-1 min-w-0 break-words">
                                                    {cert.title}
                                                </div>
                                                <div className="flex-shrink-0 w-[clamp(0.375rem,1vw,0.5rem)] h-[clamp(0.375rem,1vw,0.5rem)] rounded-full bg-teal shadow-[0_0_8px_rgba(33,128,141,0.6)] animate-pulse" />
                                            </div>
                                            <div className="text-charcoal/70 text-[clamp(0.625rem,1.5vw,0.875rem)] mb-[clamp(0.375rem,1.5vw,0.5rem)] leading-relaxed">
                                                {cert.description}
                                            </div>
                                            <div className="text-[clamp(0.5rem,1vw,0.625rem)] font-mono text-charcoal/50 tracking-widest uppercase break-words">
                                                CODE: {cert.code}
                                            </div>

                                            {/* Bottom indicator */}
                                            <div className="absolute bottom-0 left-0 right-0 h-[2px] bg-gradient-to-r from-teal/0 via-teal/40 to-teal/0 group-hover:via-teal transition-all" />
                                        </div>
                                    ))}
                                </div>

                                {/* Footer status bar */}
                                <div className="mt-[clamp(1.5rem,4vw,2rem)] pt-[clamp(1rem,3vw,1.5rem)] border-t border-teal/20 flex flex-col sm:flex-row sm:items-center sm:justify-between gap-[clamp(0.75rem,2vw,1rem)]">
                                    <div className="flex items-center gap-[clamp(0.5rem,2vw,0.75rem)]">
                                        <div className="w-[clamp(0.5rem,1.5vw,0.75rem)] h-[clamp(0.5rem,1.5vw,0.75rem)] rounded-full bg-teal shadow-[0_0_10px_rgba(33,128,141,0.6)] animate-pulse flex-shrink-0" />
                                        <span className="text-[clamp(0.625rem,1.5vw,0.875rem)] font-mono text-charcoal/70 tracking-wide whitespace-nowrap">
                                            ALL SYSTEMS OPERATIONAL
                                        </span>
                                    </div>
                                    <div className="text-[clamp(0.625rem,1.5vw,0.75rem)] font-mono text-charcoal/50 tracking-wider whitespace-nowrap">
                                        LAST UPDATED: {new Date().toLocaleDateString('en-US', { month: 'short', year: 'numeric' }).toUpperCase()}
                                    </div>
                                </div>
                            </div>
                        </motion.div>
                    </div>
                </div>
            </section>
        </Layout>
    );
}

