import React from "react";
import { Layout } from "@/components/Layout";
import { motion, useReducedMotion } from "framer-motion";
import { Card } from "@/components/ui/card";
import { FaBrain, FaNetworkWired, FaCodeBranch, FaShieldAlt, FaMedal, FaCertificate } from "react-icons/fa";

/**
 * Certifications ‚Äî Military Tactical Design with Original Colors
 * - Hexagonal grid patterns and angular borders
 * - Original teal/navy/cream color scheme with military styling
 * - Rank badge inspired certification cards
 * - Fully responsive across all device sizes
 * - Military-grade visual hierarchy with brand colors
 */

export default function Certifications() {
    const prefersReduced = useReducedMotion();

    const fadeInUp = {
        hidden: { opacity: 0, y: prefersReduced ? 0 : 30 },
        visible: { opacity: 1, y: 0, transition: { duration: 0.6, ease: [0.22, 1, 0.36, 1] } },
    } as const;

    const fadeInScale = {
        hidden: { opacity: 0, scale: prefersReduced ? 1 : 0.95 },
        visible: { opacity: 1, scale: 1, transition: { duration: 0.5, ease: "easeOut" } },
    } as const;

    const staggerContainer = {
        hidden: { opacity: 0 },
        visible: {
            opacity: 1,
            transition: { staggerChildren: prefersReduced ? 0 : 0.12 },
        },
    } as const;

    const certifications = [
        {
            icon: <FaBrain className="w-7 h-7" />,
            title: "AI Research Excellence",
            organization: "OpenAI Foundation",
            date: "March 2024",
            description:
                "Advanced certification in tactical artificial intelligence applications for defense systems.",
            level: "LEVEL IV",
            code: "AI-SEC-024",
        },
        {
            icon: <FaNetworkWired className="w-7 h-7" />,
            title: "Neural Network Architecture",
            organization: "Deep Learning Institute",
            date: "January 2024",
            description:
                "Specialized accreditation in developing hardened neural networks for mission-critical applications.",
            level: "LEVEL V",
            code: "NN-DEF-124",
        },
        {
            icon: <FaCodeBranch className="w-7 h-7" />,
            title: "Open Source Contribution",
            organization: "Global OSS Alliance",
            date: "Ongoing",
            description:
                "Recognition for contributions to secure, hardened open-source frameworks for defense applications.",
            level: "ACTIVE",
            code: "OSS-CTB-089",
        },
    ];

    const industryCertifications = [
        { title: "ISO 9001:2015", description: "Quality Management System", code: "QMS-001" },
        { title: "AS9100D", description: "Aerospace Quality Standard", code: "AQS-100" },
        { title: "ISO/IEC 27001", description: "Information Security Management", code: "ISM-270" },
        { title: "CMMC Level 5", description: "Cybersecurity Maturity Model", code: "CMM-005" },
        { title: "DO-178C", description: "Avionics Software Certification", code: "AVC-178" },
        { title: "MIL-STD Compliance", description: "Multiple Standards Verified", code: "MIL-STD" },
    ];

    return (
        <Layout
            title="Professional Certifications | TRINETHRA DEFENTECH"
            description="Our comprehensive professional qualifications and industry certifications ensure we meet the highest standards for defense technology development."
            pageType="certifications"
        >
            {/* Hexagonal pattern background */}
            <div className="fixed inset-0 pointer-events-none opacity-[0.02] z-0">
                <div className="absolute inset-0" style={{
                    backgroundImage: `url("data:image/svg+xml,%3Csvg width='60' height='52' viewBox='0 0 60 52' xmlns='http://www.w3.org/2000/svg'%3E%3Cpath d='M30 0l25.98 15v30L30 60 4.02 45V15z' fill='none' stroke='%2321808d' stroke-width='1'/%3E%3C/svg%3E")`,
                    backgroundSize: '60px 52px'
                }} />
            </div>

            <section
                className="relative pt-[clamp(1.5rem,5vw,2rem)] sm:pt-[clamp(1.75rem,6vw,2.25rem)] lg:pt-[clamp(2rem,7vw,2.5rem)] pb-[clamp(0.875rem,4vw,1.5rem)] sm:pb-[clamp(1rem,5vw,1.75rem)] lg:pb-[clamp(1.25rem,6vw,2rem)] overflow-hidden"
                aria-labelledby="certifications-heading"
            >
                {/* Tactical grid overlay */}
                <div className="absolute inset-0 opacity-[0.015] pointer-events-none">
                    <div className="absolute inset-0 bg-[linear-gradient(rgba(33,128,141,0.08)_1px,transparent_1px),linear-gradient(90deg,rgba(33,128,141,0.08)_1px,transparent_1px)] bg-[size:40px_40px]" />
                </div>

                {/* Corner brackets - Responsive sizing */}
                <div className="absolute top-[clamp(1rem,6vw,1.5rem)] left-[clamp(0.5rem,3vw,1rem)] w-[clamp(0.75rem,4vw,1.25rem)] h-[clamp(0.75rem,4vw,1.25rem)] border-l-2 border-t-2 border-teal/30" />
                <div className="absolute top-[clamp(1rem,6vw,1.5rem)] right-[clamp(0.5rem,3vw,1rem)] w-[clamp(0.75rem,4vw,1.25rem)] h-[clamp(0.75rem,4vw,1.25rem)] border-r-2 border-t-2 border-teal/30" />
                <div className="absolute bottom-[clamp(0.5rem,3vw,1rem)] left-[clamp(0.5rem,3vw,1rem)] w-[clamp(0.75rem,4vw,1.25rem)] h-[clamp(0.75rem,4vw,1.25rem)] border-l-2 border-b-2 border-teal/30" />
                <div className="absolute bottom-[clamp(0.5rem,3vw,1rem)] right-[clamp(0.5rem,3vw,1rem)] w-[clamp(0.75rem,4vw,1.25rem)] h-[clamp(0.75rem,4vw,1.25rem)] border-r-2 border-b-2 border-teal/30" />

                <div className="relative mx-auto max-w-7xl px-[clamp(1rem,4vw,2rem)] overflow-x-hidden">
                    <div className="max-w-6xl mx-auto">
                        {/* Military Header */}
                        <motion.div
                            className="text-center mb-[clamp(1.25rem,6vw,2rem)] sm:mb-[clamp(1.5rem,7vw,2.25rem)] md:mb-[clamp(2rem,8vw,2.5rem)]"
                            initial="hidden"
                            animate="visible"
                            variants={staggerContainer}
                        >
                            <motion.div
                                className="inline-flex items-center gap-[clamp(0.375rem,2vw,0.75rem)] mb-[clamp(0.75rem,3vw,1rem)] px-[clamp(0.75rem,3vw,1rem)] py-[clamp(0.375rem,1.5vw,0.5rem)] bg-teal/10 border border-teal/30 rounded backdrop-blur-sm"
                                variants={fadeInScale}
                            >
                                <FaShieldAlt className="w-[clamp(0.875rem,2vw,1.125rem)] h-[clamp(0.875rem,2vw,1.125rem)] text-teal flex-shrink-0" />
                                <span className="text-[clamp(0.625rem,1.5vw,0.875rem)] font-mono font-bold text-teal tracking-wider whitespace-nowrap">
                                    CERTIFIED OPERATIONS
                                </span>
                            </motion.div>

                            <motion.h1
                                id="certifications-heading"
                                className="font-condensed font-black mb-[clamp(0.75rem,3vw,1rem)] leading-tight [text-wrap:balance] text-[clamp(1.75rem,6vw,3.5rem)] uppercase tracking-tight"
                                variants={fadeInUp}
                                style={{
                                    background: 'linear-gradient(135deg, #21808d 0%, #29b2c6 50%, #1d7480 100%)',
                                    WebkitBackgroundClip: 'text',
                                    WebkitTextFillColor: 'transparent',
                                    backgroundClip: 'text',
                                    textShadow: '0 0 40px rgba(33, 128, 141, 0.15)'
                                }}
                            >
                                Mission Certifications
                            </motion.h1>

                            <motion.div
                                className="h-1 w-[clamp(1.5rem,8vw,2rem)] mx-auto mb-[clamp(0.75rem,3vw,1rem)] bg-gradient-to-r from-transparent via-teal to-transparent"
                                variants={fadeInUp}
                            />

                            <motion.p
                                className="text-[clamp(0.875rem,2vw,1.125rem)] text-charcoal/80 max-w-3xl mx-auto [text-wrap:pretty] leading-relaxed px-[clamp(0.5rem,2vw,1rem)]"
                                variants={fadeInUp}
                            >
                                Comprehensive professional qualifications and industry certifications ensuring compliance with the
                                highest defense technology standards.
                            </motion.p>
                        </motion.div>

                        {/* Featured Certifications - Military Cards */}
                        <motion.div
                            className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-[clamp(1rem,3vw,1.5rem)] mb-[clamp(2rem,6vw,2.5rem)]"
                            initial="hidden"
                            whileInView="visible"
                            viewport={{ once: true, amount: 0.2 }}
                            variants={staggerContainer}
                        >
                            {certifications.map((cert, index) => (
                                <motion.div key={index} variants={fadeInUp} className="min-w-0">
                                    <Card className="relative bg-white border-2 border-teal/20 rounded-lg shadow-[0_4px_20px_rgba(33,128,141,0.08)] p-[clamp(1rem,3vw,1.5rem)] h-full transition-all duration-300 hover:border-teal/40 hover:shadow-[0_8px_30px_rgba(33,128,141,0.15)] focus-within:border-teal/40 group overflow-hidden">
                                        {/* Corner accents */}
                                        <div className="absolute top-0 left-0 w-[clamp(0.5rem,2vw,0.75rem)] h-[clamp(0.5rem,2vw,0.75rem)] border-l-2 border-t-2 border-teal/40 transition-all group-hover:w-[clamp(0.75rem,3vw,1rem)] group-hover:h-[clamp(0.75rem,3vw,1rem)]" />
                                        <div className="absolute bottom-0 right-0 w-[clamp(0.5rem,2vw,0.75rem)] h-[clamp(0.5rem,2vw,0.75rem)] border-r-2 border-b-2 border-teal/40 transition-all group-hover:w-[clamp(0.75rem,3vw,1rem)] group-hover:h-[clamp(0.75rem,3vw,1rem)]" />

                                        {/* Level badge */}
                                        <div className="flex items-center justify-between mb-[clamp(0.75rem,2vw,1rem)] gap-2">
                                            <div className="flex items-center gap-[clamp(0.375rem,1.5vw,0.5rem)] px-[clamp(0.5rem,2vw,0.75rem)] py-[clamp(0.125rem,1vw,0.25rem)] bg-teal/10 border border-teal/30 rounded flex-shrink-0">
                                                <FaMedal className="w-[clamp(0.625rem,1.5vw,0.75rem)] h-[clamp(0.625rem,1.5vw,0.75rem)] text-[#FFD700] flex-shrink-0" />
                                                <span className="text-[clamp(0.625rem,1vw,0.75rem)] font-mono font-bold text-teal tracking-wider whitespace-nowrap">
                                                    {cert.level}
                                                </span>
                                            </div>
                                            <span className="text-[clamp(0.625rem,1vw,0.75rem)] font-mono text-charcoal/50 tracking-wider whitespace-nowrap">
                                                {cert.code}
                                            </span>
                                        </div>

                                        {/* Icon */}
                                        <div className="relative bg-gradient-to-br from-teal/15 to-teal/5 rounded-lg w-[clamp(3rem,8vw,4rem)] h-[clamp(3rem,8vw,4rem)] flex items-center justify-center mb-[clamp(0.75rem,3vw,1.25rem)] text-teal border border-teal/20 shadow-[inset_0_2px_10px_rgba(33,128,141,0.05)] flex-shrink-0">
                                            <div className="w-[clamp(1.5rem,4vw,2rem)] h-[clamp(1.5rem,4vw,2rem)]">
                                                {cert.icon}
                                            </div>
                                            <div className="absolute inset-0 bg-gradient-to-t from-teal/5 to-transparent rounded-lg" />
                                        </div>

                                        {/* Content */}
                                        <h3 className="text-[clamp(1rem,2.5vw,1.25rem)] font-condensed font-bold mb-[clamp(0.375rem,1.5vw,0.625rem)] text-navy [text-wrap:balance] uppercase tracking-tight">
                                            {cert.title}
                                        </h3>
                                        <p className="text-charcoal/80 mb-[clamp(0.25rem,1vw,0.375rem)] text-[clamp(0.75rem,1.5vw,0.875rem)] font-semibold tracking-wide">
                                            {cert.organization}
                                        </p>
                                        <p className="text-teal/70 text-[clamp(0.625rem,1vw,0.75rem)] mb-[clamp(0.5rem,2vw,0.875rem)] tracking-wider uppercase font-mono">
                                            {cert.date}
                                        </p>
                                        <p className="text-charcoal/70 text-[clamp(0.75rem,1.5vw,0.875rem)] leading-relaxed [text-wrap:pretty]">
                                            {cert.description}
                                        </p>

                                        {/* Bottom accent line */}
                                        <div className="absolute bottom-0 left-0 right-0 h-1 bg-gradient-to-r from-transparent via-teal/40 to-transparent" />
                                    </Card>
                                </motion.div>
                            ))}
                        </motion.div>

                        {/* Industry Certifications - Command Grid */}
                        <motion.div
                            className="relative bg-gradient-to-br from-cream/50 to-white backdrop-blur-sm rounded-lg p-[clamp(1.5rem,4vw,2.5rem)] shadow-[inset_0_2px_20px_rgba(94,82,64,0.05),0_4px_30px_rgba(33,128,141,0.08)] border-2 border-teal/15 overflow-hidden"
                            initial={{ opacity: 0, y: prefersReduced ? 0 : 20 }}
                            whileInView={{ opacity: 1, y: 0 }}
                            viewport={{ once: true, amount: 0.2 }}
                            transition={{ duration: 0.6 }}
                        >
                            {/* Background tactical pattern */}
                            <div className="absolute inset-0 opacity-[0.03]">
                                <div className="absolute inset-0 bg-[linear-gradient(45deg,transparent_48%,rgba(33,128,141,0.3)_48%,rgba(33,128,141,0.3)_52%,transparent_52%),linear-gradient(-45deg,transparent_48%,rgba(33,128,141,0.3)_48%,rgba(33,128,141,0.3)_52%,transparent_52%)] bg-[length:20px_20px]" />
                            </div>

                            <div className="relative">
                                {/* Header */}
                                <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between gap-[clamp(0.75rem,3vw,1rem)] mb-[clamp(1.5rem,4vw,2rem)]">
                                    <div className="flex-1">
                                        <div className="inline-flex items-center gap-[clamp(0.375rem,1.5vw,0.5rem)] mb-[clamp(0.5rem,2vw,0.75rem)] px-[clamp(0.5rem,2vw,0.75rem)] py-[clamp(0.125rem,1vw,0.25rem)] bg-teal/10 border border-teal/30 rounded">
                                            <FaCertificate className="w-[clamp(0.625rem,1.5vw,0.75rem)] h-[clamp(0.625rem,1.5vw,0.75rem)] text-teal flex-shrink-0" />
                                            <span className="text-[clamp(0.625rem,1.5vw,0.75rem)] font-mono font-bold text-teal tracking-wider whitespace-nowrap">
                                                STANDARDS COMPLIANCE
                                            </span>
                                        </div>
                                        <h2 className="font-condensed font-bold text-navy leading-snug [text-wrap:balance] text-[clamp(1.25rem,3.5vw,1.75rem)] uppercase tracking-tight">
                                            Industry Certifications
                                        </h2>
                                    </div>
                                    <div className="text-[clamp(0.625rem,1.5vw,0.75rem)] font-mono text-charcoal/60 tracking-wider whitespace-nowrap">
                                        06 ACTIVE CERTIFICATIONS
                                    </div>
                                </div>

                                {/* Grid */}
                                <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-[clamp(1rem,2.5vw,1.25rem)]">
                                    {industryCertifications.map((cert, index) => (
                                        <div
                                            key={index}
                                            className="relative bg-white p-[clamp(0.75rem,2vw,1rem)] rounded border-l-4 border-teal shadow-[0_2px_10px_rgba(33,128,141,0.08)] hover:shadow-[0_4px_20px_rgba(33,128,141,0.15)] transition-all duration-300 hover:translate-x-1 min-w-0 group overflow-hidden"
                                        >
                                            {/* Scan line effect */}
                                            <div className="absolute inset-0 bg-gradient-to-b from-transparent via-teal/5 to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-500 pointer-events-none" />

                                            <div className="relative flex items-start justify-between gap-[clamp(0.5rem,2vw,0.75rem)] mb-[clamp(0.375rem,1.5vw,0.5rem)]">
                                                <div className="font-black text-navy text-[clamp(0.625rem,1.5vw,0.875rem)] tracking-wider uppercase font-condensed flex-1 min-w-0 break-words">
                                                    {cert.title}
                                                </div>
                                                <div className="flex-shrink-0 w-[clamp(0.375rem,1vw,0.5rem)] h-[clamp(0.375rem,1vw,0.5rem)] rounded-full bg-teal shadow-[0_0_8px_rgba(33,128,141,0.6)] animate-pulse" />
                                            </div>
                                            <div className="text-charcoal/70 text-[clamp(0.625rem,1.5vw,0.875rem)] mb-[clamp(0.375rem,1.5vw,0.5rem)] leading-relaxed">
                                                {cert.description}
                                            </div>
                                            <div className="text-[clamp(0.5rem,1vw,0.625rem)] font-mono text-charcoal/50 tracking-widest uppercase break-words">
                                                CODE: {cert.code}
                                            </div>

                                            {/* Bottom indicator */}
                                            <div className="absolute bottom-0 left-0 right-0 h-[2px] bg-gradient-to-r from-teal/0 via-teal/40 to-teal/0 group-hover:via-teal transition-all" />
                                        </div>
                                    ))}
                                </div>

                                {/* Footer status bar */}
                                <div className="mt-[clamp(1.5rem,4vw,2rem)] pt-[clamp(1rem,3vw,1.5rem)] border-t border-teal/20 flex flex-col sm:flex-row sm:items-center sm:justify-between gap-[clamp(0.75rem,2vw,1rem)]">
                                    <div className="flex items-center gap-[clamp(0.5rem,2vw,0.75rem)]">
                                        <div className="w-[clamp(0.5rem,1.5vw,0.75rem)] h-[clamp(0.5rem,1.5vw,0.75rem)] rounded-full bg-teal shadow-[0_0_10px_rgba(33,128,141,0.6)] animate-pulse flex-shrink-0" />
                                        <span className="text-[clamp(0.625rem,1.5vw,0.875rem)] font-mono text-charcoal/70 tracking-wide whitespace-nowrap">
                                            ALL SYSTEMS OPERATIONAL
                                        </span>
                                    </div>
                                    <div className="text-[clamp(0.625rem,1.5vw,0.75rem)] font-mono text-charcoal/50 tracking-wider whitespace-nowrap">
                                        LAST UPDATED: {new Date().toLocaleDateString('en-US', { month: 'short', year: 'numeric' }).toUpperCase()}
                                    </div>
                                </div>
                            </div>
                        </motion.div>
                    </div>
                </div>
            </section>
        </Layout>
    );
}

