import React, { useState } from "react";
import { Layout } from "@/components/Layout";
import { motion, useReducedMotion } from "framer-motion";
import { Card } from "@/components/ui/card";
import { FaBrain, FaNetworkWired, FaCodeBranch, FaShieldAlt, FaMedal, FaCertificate, FaArrowRight } from "react-icons/fa";

export default function Certifications() {
    const prefersReduced = useReducedMotion();
    const [hoveredCert, setHoveredCert] = useState<number | null>(null);

    const fadeInUp = {
        hidden: { opacity: 0, y: prefersReduced ? 0 : 40 },
        visible: { opacity: 1, y: 0, transition: { duration: 0.6, ease: [0.22, 1, 0.36, 1] } },
    } as const;

    const fadeInScale = {
        hidden: { opacity: 0, scale: prefersReduced ? 1 : 0.92 },
        visible: { opacity: 1, scale: 1, transition: { duration: 0.6, ease: "easeOut" } },
    } as const;

    const staggerContainer = {
        hidden: { opacity: 0 },
        visible: {
            opacity: 1,
            transition: { staggerChildren: prefersReduced ? 0 : 0.1, delayChildren: 0.1 },
        },
    } as const;

    const certifications = [
        {
            icon: <FaBrain className="w-full h-full" />,
            title: "AI Research Excellence",
            organization: "OpenAI Foundation",
            date: "March 2024",
            description:
                "Advanced certification in tactical artificial intelligence applications for defense systems.",
            level: "LEVEL IV",
            code: "AI-SEC-024",
            color: "from-teal/20 to-teal/5",
        },
        {
            icon: <FaNetworkWired className="w-full h-full" />,
            title: "Neural Network Architecture",
            organization: "Deep Learning Institute",
            date: "January 2024",
            description:
                "Specialized accreditation in developing hardened neural networks for mission-critical applications.",
            level: "LEVEL V",
            code: "NN-DEF-124",
            color: "from-teal/20 to-teal/5",
        },
        {
            icon: <FaCodeBranch className="w-full h-full" />,
            title: "Open Source Contribution",
            organization: "Global OSS Alliance",
            date: "Ongoing",
            description:
                "Recognition for contributions to secure, hardened open-source frameworks for defense applications.",
            level: "ACTIVE",
            code: "OSS-CTB-089",
            color: "from-teal/20 to-teal/5",
        },
    ];

    const industryCertifications = [
        { title: "ISO 9001:2015", description: "Quality Management System", code: "QMS-001" },
        { title: "AS9100D", description: "Aerospace Quality Standard", code: "AQS-100" },
        { title: "ISO/IEC 27001", description: "Information Security Management", code: "ISM-270" },
        { title: "CMMC Level 5", description: "Cybersecurity Maturity Model", code: "CMM-005" },
        { title: "DO-178C", description: "Avionics Software Certification", code: "AVC-178" },
        { title: "MIL-STD Compliance", description: "Multiple Standards Verified", code: "MIL-STD" },
    ];

    return (
        <Layout
            title="Professional Certifications | TRINETHRA DEFENTECH"
            description="Our comprehensive professional qualifications and industry certifications ensure we meet the highest standards for defense technology development."
            pageType="certifications"
        >
            <div className="fixed inset-0 pointer-events-none opacity-[0.015] z-0">
                <div className="absolute inset-0 bg-[radial-gradient(circle_at_20%_50%,rgba(33,128,141,0.1)_0%,transparent_50%),radial-gradient(circle_at_80%_80%,rgba(33,128,141,0.08)_0%,transparent_50%)]" />
            </div>

            <section
                className="relative pt-[clamp(3rem,10vw,5rem)] pb-[clamp(3rem,10vw,5rem)] overflow-hidden"
                aria-labelledby="certifications-heading"
            >
                <div className="relative mx-auto max-w-7xl px-[clamp(1rem,4vw,2rem)]">
                    {/* Hero Header */}
                    <motion.div
                        className="max-w-4xl mx-auto mb-[clamp(3rem,8vw,4rem)]"
                        initial="hidden"
                        animate="visible"
                        variants={staggerContainer}
                    >
                        <motion.div
                            className="inline-flex items-center gap-2 mb-6 px-4 py-2 bg-teal/10 border border-teal/30 rounded-full"
                            variants={fadeInScale}
                        >
                            <FaShieldAlt className="w-4 h-4 text-teal" />
                            <span className="text-sm font-mono font-bold text-teal tracking-wider">
                                PROFESSIONAL CREDENTIALS
                            </span>
                        </motion.div>

                        <motion.h1
                            id="certifications-heading"
                            className="text-[clamp(2.5rem,7vw,4.5rem)] font-bold leading-tight mb-6 text-navy"
                            variants={fadeInUp}
                        >
                            Certified <span className="bg-gradient-to-r from-teal to-teal/70 bg-clip-text text-transparent">Excellence</span> in Defense Technology
                        </motion.h1>

                        <motion.p
                            className="text-[clamp(1rem,2vw,1.25rem)] text-charcoal/70 leading-relaxed max-w-2xl"
                            variants={fadeInUp}
                        >
                            Our comprehensive professional qualifications and industry certifications ensure we deliver the highest standards in defense technology development and innovation.
                        </motion.p>
                    </motion.div>

                    {/* Featured Certifications - Horizontal Flow */}
                    <motion.div
                        className="mb-[clamp(4rem,10vw,6rem)]"
                        initial="hidden"
                        whileInView="visible"
                        viewport={{ once: true, amount: 0.2 }}
                        variants={staggerContainer}
                    >
                        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 md:gap-8">
                            {certifications.map((cert, index) => (
                                <motion.div
                                    key={index}
                                    variants={fadeInUp}
                                    className="group h-full"
                                    onMouseEnter={() => !prefersReduced && setHoveredCert(index)}
                                    onMouseLeave={() => setHoveredCert(null)}
