                                    <Card className="relative h-full bg-white border border-teal/20 rounded-2xl p-8 shadow-lg transition-all duration-500 hover:shadow-2xl hover:border-teal/40 overflow-hidden">
                                        {/* Gradient overlay on hover */}
                                        <div className="absolute inset-0 opacity-0 group-hover:opacity-100 transition-opacity duration-500 pointer-events-none bg-gradient-to-br from-teal/5 to-transparent" />

                                        {/* Top accent line */}
